// board has a single neopixel
#define NUMPIXELS 1

// https://www.adafruit.com/product/3357
#define VS1053_RESET   -1     // VS1053 reset pin (not used!) - reset is linked to microcontroller reset
#define VS1053_CS       6     // VS1053 chip select pin (output)
#define VS1053_DCS     10     // VS1053 Data/command select pin (output)
#define CARDCS          5     // Card chip select pin
// DREQ should be an Int pin *if possible* (not possible on 32u4)
#define VS1053_DREQ     9     // VS1053 Data request, ideally an Interrupt pin

#define DEVICE_ID "featheresp32s2"
#define DEVICE_NAME "Chime"
#define DEVICE_MANUFACTURER "Adafruit"
#define DEVICE_MODEL "Feather ESP32-S2 with BME280"
#define DEVICE_VERSION "20230331.0900"

// Directory on SDcard where tones are stored. 
#define TONE_DIR "/tones"

// Display
// Used for I2C or SPI
#define OLED_RESET -1
#define DISPLAY_WIDTH 128
#define DISPLAY_HEIGHT 128

